        // Animate elements when scrolling
        const animateOnScroll = () => {
            const elements = document.querySelectorAll('.feature-card, .about-text, .about-image, .gallery-item, .info-card');

            elements.forEach(element => {
                const elementPosition = element.getBoundingClientRect().top;
                const screenPosition = window.innerHeight / 1.2;

                if (elementPosition < screenPosition) {
                    element.style.opacity = '1';
                    element.style.transform = 'translateY(0)';
                }
            });
        };

        // Set initial styles for animation
        document.addEventListener('DOMContentLoaded', () => {
            const animatedElements = document.querySelectorAll('.feature-card, .about-text, .about-image, .gallery-item, .info-card');

            animatedElements.forEach(element => {
                element.style.opacity = '0';
                element.style.transform = 'translateY(30px)';
                element.style.transition = 'all 0.6s ease-out';
            });

            // Trigger initial check
            animateOnScroll();
        });

        // Add scroll event listener
        window.addEventListener('scroll', animateOnScroll);
    </script>
