        // Smooth scrolling for the page
        document.querySelector('.scroll-down').addEventListener('click', function() {
            document.querySelector('.container').scrollIntoView({ 
                behavior: 'smooth' 
            });
        });

        // Parallax effect for night-life section
        window.addEventListener('scroll', function() {
            const scrollPosition = window.pageYOffset;
            const nightLife = document.querySelector('.night-life');
            nightLife.style.backgroundPositionY = scrollPosition * 0.5 + 'px';
        });

        // Simple animation for features when they come into view
        const features = document.querySelectorAll('.feature');

        const featureObserver = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.style.opacity = '1';
                    entry.target.style.transform = 'translateY(0)';
                }
            });
        }, { threshold: 0.1 });

        features.forEach(feature => {
            feature.style.opacity = '0';
            feature.style.transform = 'translateY(20px)';
            feature.style.transition = 'all 0.5s ease-out';
            featureObserver.observe(feature);
        });
    </script>
</body>
</html>
