        function reveal() {
            const reveals = document.querySelectorAll('.card, .gallery-item, .info-box, .fact-box');

            for(let i = 0; i < reveals.length; i++) {
                const windowHeight = window.innerHeight;
                const revealTop = reveals[i].getBoundingClientRect().top;
                const revealPoint = 150;

                if(revealTop < windowHeight - revealPoint) {
                    reveals[i].style.opacity = '1';
                    reveals[i].style.transform = 'translateY(0)';
                }
            }
        }

        // Initialize elements with opacity 0 for animation
        document.addEventListener('DOMContentLoaded', function() {
            const elementsToAnimate = document.querySelectorAll('.card, .gallery-item, .info-box, .fact-box');
            elementsToAnimate.forEach(el => {
                el.style.opacity = '0';
                el.style.transform = 'translateY(20px)';
                el.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
            });

            // Trigger the animation
            setTimeout(reveal, 100);
        });
    </script>
