        // Scroll animation
        document.addEventListener('DOMContentLoaded', function() {
            const sections = document.querySelectorAll('section');

            const observer = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        entry.target.classList.add('visible');
                    }
                });
            }, {
                threshold: 0.1
            });

            sections.forEach(section => {
                observer.observe(section);
            });
        });

        // Smooth scroll
        function scrollToSection(id) {
            const element = document.getElementById(id);
            window.scrollTo({
                top: element.offsetTop - 80,
                behavior: 'smooth'
            });
        }

        // Change gallery images on click
        document.addEventListener('DOMContentLoaded', function() {
            const galleryItems = document.querySelectorAll('.gallery-item');

            galleryItems.forEach(item => {
                item.addEventListener('click', function() {
                    // You can add a lightbox functionality here if needed
                });
            });

            // Auto rotate header background
            let currentBg = 0;
            const bgImages = [
                'linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.7)), url(https://antarejatour.com/wp-content/uploads/2019/09/candi-barong-jogjaku.jpg)',
                'linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.7)), url(http://jogja.korinatour.co.id/wp-content/uploads/2016/12/candi-barong-4.jpg)',
                'linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.7)), url(https://www.dejava.net/dejogja/wp-content/uploads/2021/12/Daya-Tarik-Candi-Barong-Sleman.jpg)'
            ];

            const heroImage = document.querySelector('.hero-image');

            setInterval(() => {
                currentBg = (currentBg + 1) % bgImages.length;
                heroImage.style.backgroundImage = bgImages[currentBg];
            }, 8000);
        });
    </script>
