        function revealOnScroll() {
            const elements = document.querySelectorAll('.feature-card, .reason, .gallery-item');

            for (let i = 0; i < elements.length; i++) {
                const element = elements[i];
                const elementPosition = element.getBoundingClientRect().top;
                const screenPosition = window.innerHeight / 1.2;

                if (elementPosition < screenPosition) {
                    element.style.opacity = '1';
                    element.style.transform = 'translateY(0)';
                }
            }
        }

        // Initialize elements with opacity 0 for animation
        document.addEventListener('DOMContentLoaded', function() {
            const features = document.querySelectorAll('.feature-card');
            const reasons = document.querySelectorAll('.reason');
            const galleryItems = document.querySelectorAll('.gallery-item');

            features.forEach(feature => {
                feature.style.opacity = '0';
                feature.style.transform = 'translateY(20px)';
                feature.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
            });

            reasons.forEach(reason => {
                reason.style.opacity = '0';
                reason.style.transform = 'scale(0.9)';
                reason.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
            });

            galleryItems.forEach(item => {
                item.style.opacity = '0';
                item.style.transform = 'translateY(20px)';
                item.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
            });

            // Trigger initial scroll check
            revealOnScroll();
        });
    </script>
